package ch05;

import jakarta.servlet.ServletException;
import jakarta.servlet.annotation.WebServlet;
import jakarta.servlet.http.HttpServlet;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;
import java.io.IOException;
import java.io.PrintWriter;


public class GBBServlet extends HttpServlet {
	private static final long serialVersionUID = 1L;
       
    public GBBServlet() {
        super();    
    }

	protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		String[] gbb = { "gawi.jpg" ,"bawi.jpg","bo.jpg"};
		//String[] result = {"win.jpg", "lose.jpg", "draw.jpg"};	//결과	
		
		int my = Integer.parseInt(request.getParameter("my"));
		//System.out.println("my: " + my);
		
		int com = (int) (Math.random() * 3);
		System.out.println("com: " + com);
		
		//내가 낸것 //컴퓨터가 낸것 // 이미지로 Display
		response.setContentType("text/html; char=utf-8");
		
		PrintWriter out = response.getWriter();
	
		out.print("<!DOCTYPE html><html><head><title>가위바위보게임</title></head><body><table>");
		 out.print("<tr><td>my</td><td>com</td></tr>");
		 out.print("<tr><td><img alt='내가 낸것' src='img/" + gbb[my] + "' height='150'></td>");
		out.print("<td><img alt='com이 낸것' src='img/" + gbb[com] + "' height='150'></td></tr>");
		  out.print("</table><p>" +gbb[my]+" "+gbb[com]+ "<img src=''></body></html>");
		out.close();
	}

	protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		doGet(request, response);
		
	}
}


